const texto = $input.all().map(item => item.json?.descricao || "").join(",").trim();

let produtoEscolhido;
const regex = /Produto:\*([\s\S]*?)(?=\{\*\})/;
const resultado = texto.match(regex);

const resultadoFinal = resultado ? resultado[1] : "Conteúdo não encontrado";
let campoformatado = resultadoFinal.toUpperCase();

const regex2 = /[X] ([\s\S]*?)\r\n\r\n/;
const resultado2 = campoformatado.match(regex2);

produtoEscolhido = resultado2 ? resultado2[1] : "Conteúdo não encontrado";

return [
  {
    json: {
      tipo: $input.first().json.tipo,
      issue: $input.first().json.issue,
      teste: campoformatado,
      produto: produtoEscolhido
    }
  }
];

