let texto = $input.all().map(item => item.json?.release || "").join(",").trim();
let preenchido = "";

const referencia = `Programas: xxxx xxxx (dica: escrever os projetos alterados separados por espaço)\r\nDescrição da alteração: ***REMOVIDO*** (dica: descrever para leitura do cliente)\r\nDocumentação: ***REMOVIDO*** (dica: colar link do Linx Share. obs: remover essa linha se não houver)\r\nMenu: xxxx > xxxx > xxxx (dica: separar por \">\" os níveis do menu)`;

if(!texto.includes(referencia)){
  preenchido = "Release preenchido";
}else{
  preenchido = "Release não foi preenchido corretamente";
}

return [
  {
    json: {
      release_preenchido: preenchido,
      issue: $input.first().json.issue
    }
  }
];
